ksel1.text('上传中');										}					}, 200);				} else {					$('#img1_gksel_error').html('上传失败');					return false;				}			},			onComplete: function(file, response){				button_gksel1.text('上传图片');										window.clearInterval(interval);				this.enable();				if(response=='false'){					$('#img1_gksel_error').html('上传失败');				}else{					var pic = eval("("+response+")");					$('#img1_gksel_show').html('<img style="float:left;max-width:400px;max-height:400px;" src="'+baseurl+pic.logo+'" />');					$('#img1_gksel').attr('value',pic.logo);					$('#img1_gksel_error').html('');				}				}		});	}})</script><?php $this->load->view('admin/footer')?>