textarea[name="date_address_id_lunch_memo"]').val();					//----定义多语言的字段------START			var GETLANGOBJ = new Array();			var GETLANGOBJ_num = 0;		//----定义多语言的字段------END				var postOBJ = new Object();		postOBJ.backurl = backurl;		postOBJ.date_address_id_lunch_memo = date_address_id_lunch_memo;		$.post(baseurl+'index.php/admins/order/meals_shipping_lunch/'+day_id, postOBJ, function (data){			var obj = eval( "(" + data + ")" );			actionsubmit_button.html('<img class="icon_success" src="'+baseurl+'themes/default/images/global_ok.png"/><span>'+L['cy_save_success']+'</span>');			location.href = obj.backurl;		})	}}//晚餐function toship_dishinfo_dinner(day_id){	var lancodelist = getlancodelist();	if(isajaxsaveing == 0){		//具体点击的按钮		actionsubmit_button = $('div[onclick="toship_dishinfo_dinner('+day_id+')"]');		//ajax正在保存中		isajaxsaveing = 1;		//返回url		var backurl = $('input[name="backurl"]').val();		//将提交按钮设置为保存中		actionsubmit_button.attr('class', 'gksel_btn_action_off');		actionsubmit_button.html('<img class="icon_loading" src="'+baseurl+'themes/default/images/ajax_loading.gif"/><span>'+L['cy_saving']+'...</span>');				var date_address_id_dinner_memo = $('textarea[name="date_address_id_dinner_memo"]').val();					//----定义多语言的字段------START			var GETLANGOBJ = new Array();			var GETLANGOBJ_num = 0;		//----定义多语言的字段------END				var postOBJ = new Object();		postOBJ.backurl = backurl;		postOBJ.date_address_id_dinner_memo = date_address_id_dinner_memo;		$.post(baseurl+'index.php/admins/order/meals_shipping_dinner/'+day_id, postOBJ, function (data){			var obj = eval( "(" + data + ")" );			actionsubmit_button.html('<img class="icon_success" src="'+baseurl+'themes/default/images/global_ok.png"/><span>'+L['cy_save_success']+'</span>');			location.href = obj.backurl;		})	}}//早餐和晚餐function toship_dishinfo_breakfast_dinner(day_id){	var lancodelist = getlancodelist();	if(isajaxsaveing == 0){		//具体点击的按钮		actionsubmit_button = $('div[onclick="toship_dishinfo_breakfast_dinner('+day_id+')"]');		//ajax正在保存中		isajaxsaveing = 1;		//返回url		var backurl = $('input[name="backurl"]').val();		//将提交按钮设置为保存中		actionsubmit_button.attr('class', 'gksel_btn_action_off');		actionsubmit_button.html('<img class="icon_loading" src="'+baseurl+'themes/default/images/ajax_loading.gif"/><span>'+L['cy_saving']+'...</span>');				var date_address_id_breakfast_dinner_memo = $('textarea[name="date_address_id_breakfast_dinner_memo"]').val();					//----定义多语言的字段------START			var GETLANGOBJ = new Array();			var GETLANGOBJ_num = 0;		//----定义多语言的字段------END				var postOBJ = new Object();		postOBJ.backurl = backurl;		postOBJ.date_address_id_breakfast_dinner_memo = date_address_id_breakfast_dinner_memo;		$.post(baseurl+'index.php/admins/order/meals_shipping_breakfast_dinner/'+day_id, postOBJ, function (data){			var obj = eval( "(" + data + ")" );			actionsubmit_button.html('<img class="icon_success" src="'+baseurl+'themes/default/images/global_ok.png"/><span>'+L['cy_save_success']+'</span>');			location.href = obj.backurl;		})	}}</script><?php $this->load->view('admin/footer')?>