eakfast_dinner_memo = $('textarea[name="date_address_id_breakfast_dinner_memo"]').val();											//----定义多语言的字段------START						var GETLANGOBJ = new Array();						var GETLANGOBJ_num = 0;					//----定义多语言的字段------END										var postOBJ = new Object();					postOBJ.backurl = backurl;					postOBJ.date_address_id_breakfast_dinner_memo = date_address_id_breakfast_dinner_memo;					$.post(baseurl+'index.php/admins/order/meals_shipping_breakfast_dinner/'+day_id, postOBJ, function (data){						var obj = eval( "(" + data + ")" );						actionsubmit_button.html('<img class="icon_success" src="'+baseurl+'themes/default/images/global_ok.png"/><span>'+L['cy_save_success']+'</span>');						location.href = obj.backurl;					})				}			}		</script>			</td></tr>