);			GETLANGOBJ[GETLANGOBJ_num]['field_name'] = 'whoweare_tagline';			GETLANGOBJ[GETLANGOBJ_num]['field_type'] = "textarea";			GETLANGOBJ[GETLANGOBJ_num]['field_CMNAME'] = '';		//----定义多语言的字段------END				var returnOBJ = checkjsformcontent(lancodelist, GETOBJ, GETLANGOBJ);//----定义字段变量, 检查是否为空或格式错误------START		var postOBJ = returnOBJ.postOBJ;		postOBJ.backurl = backurl;		postOBJ.GETOBJ = returnOBJ.GETOBJ_arr;		postOBJ.GETOBJ_type = returnOBJ.GETOBJ_type_arr;		postOBJ.GETOBJ_realname = returnOBJ.GETOBJ_realname_arr;		postOBJ.GETLANGOBJ = returnOBJ.GETLANGOBJ_arr;		if(returnOBJ.ispass == 1){			$.post(baseurl+'index.php/admins/whoweare/add_whoweare', postOBJ, function (data){				console.log(data);				var obj = eval( "(" + data + ")" );				actionsubmit_button.html('<img class="icon_success" src="'+baseurl+'themes/default/images/global_ok.png"/><span><?php if($this->langtype == '_ch'){echo '保存成功';}else{echo 'Success';}?></span>');				location.href = obj.backurl;			})		}else{			actionsubmit_button.attr('class', 'gksel_btn_action_on');			actionsubmit_button.html(L['cy_save']);			isajaxsaveing = 0;//ajax正在保存中 --- 释放		}	}}</script><form method="post">	<table class="gksel_normal_tabpost">		<tr>			<td align="right"><?php echo lang('dz_product_picture')?></td>			<td>				<div class="img_gksel_show" id="img1_gksel_show">					<?php 						$img1_gksel = '';					?>				</div>				<div class="img_gksel_choose" id="img1_gksel_choose">上传图片</div>				<div style="float:left;"><input type="hidden" id="img1_gksel" name="img1_gksel" value="<?php echo $img1_gksel;?>"/></div>				<div style="float:left;margin-left:5px;margin-top:5px;"><font class="fonterror" id="img1_gksel_error"><font style="color:gray;">仅支持 Jpg, Png, Gif (1000px * 1000px)</font></div>			</td>		</tr>		<tr>			<td colspan="2">				<div style="float:left;width:100%;border-top:1px solid #EFEFEF;margin:10px 0px;"></div>			</td>		</tr>		<tr>			<td align="right" width="150"><?php if($this->langtype == '_ch'){echo '案例名称';}else{echo 'whoweare name';}?> (English)</td>			<td align="left">				<input type="text" name="whoweare_name_en" value=""/>				<div class="tipsgroupbox"><div class="request">*</div></div>			</td>		</tr>		<tr>			<td align="right" width="150"><?php if($this->langtype == '_ch'){echo '案例名称';}else{echo 'whoweare name';}?> (中文)</td>			<td align="left">				<input type="text" name="whoweare_name_ch" value=""/>				<div class="tipsgroupbox"><div class="request">*</div></div>			</td>		</tr>		<tr>			<td colspan="2">				<div style="float:left;width:100%;border-top:1px solid #EFEFEF;margin:10px 0px;"></div>			</td>		</tr>		<tr>			<td align="right" width="150"><?php if($this->langtype == '_ch'){echo '案例广告语';}else{echo 'Tagline';}?> (English)</td>			<td align="left">				<textarea name="whoweare_tagline_en"></textarea>				<div class="tipsgroupbox"><div class="request">*</div></div>			</td>		</tr>		<tr>			<td align="right" width="150"><?php if($this->langtype == '_ch'){echo '案例广告语';}else{echo 'Tagline';}?> (中文)</td>			<td align="left">				<textarea name="whoweare_tagline_ch"></textarea>				<div class="tipsgroupbox"><div class="request">*</div></div>			</td>		</tr>		<tr>			<td colspan="2">				<div style="float:left;width:100%;border-top:1px solid #EFEFEF;margin:10px 0px;"></div>			</td>		</tr>		<tr>			<td align="right" width="150"><?php if($this->langtype == '_ch'){echo '状态';}else{echo 'Status';}?></td>			<td align="left">				<input name="status" type="checkbox" defaultvalue="0" class="mgc-switch" value="1" checked/>			</td>		</tr>		<tr>			<td>				<input name="backurl" type="hidden" value="<?php echo $backurl;?>"/>			</td>			<td align="left">				<div class="gksel_btn_action_on" onclick="tosave_whoweareinfo()"><?php echo lang('cy_save')?></div>			</td>		</tr>	</table></form><script type="text/javascript">$(document).ready(function(){	var button_gksel1 = $('#img1_gksel_choose'), interval;	if(button_gksel1.length>0){		new AjaxUpload(button_gksel1,{			action: baseurl+'index.php/welcome/uplogo/1000/1000',			name: 'logo',onSubmit : function(file, ext){				if (ext && /^(jpg|png|gif)$/.test(ext)){					button_gksel1.text('上传中');					this.disable();					interval = window.setInterval(function(){						var text = button_gksel1.text();						if (text.length < 13){							button_gksel1.text(text + '.');											} else {							button_gksel1.text('上传中');										}					}, 200);				} else {					$('#img1_gksel_error').html('上传失败');					return false;				}			},			onComplete: function(file, response){				button_gksel1.text('上传图片');										window.clearInterval(interval);				this.enable();				if(response=='false'){					$('#img1_gksel_error').html('上传失败');				}else{					var pic = eval("("+response+")");					$('#img1_gksel_show').html('<img style="float:left;max-width:400px;max-height:400px;" src="'+baseurl+pic.logo+'" />');					$('#img1_gksel').attr('value',pic.logo);					$('#img1_gksel_error').html('');				}				}		});	}})</script><?php $this->load->view('admin/footer')?>